<?phpinclude 'connect.php';header("Content-type: text/plain; charset=UTF-8");//sessionから個人情報を取得    session_start();    $email=$_SESSION['email'];    //eamil一致のidを取得する        $sql="SELECT id from users WHERE email='$email'";    $sth=$dbh->prepare($sql);    $sth->execute();    $result=$sth->fetchAll();    foreach($result as $row){         $id=$row['id'];    }if (isset($_POST['isFollow'])){    //ここに何かしらの処理を書く（DB登録やファイルへの書き込みなど）  //  echo "OK";  //  echo $_POST['isFollow'];  //  echo $_POST['Classname'];		$isFollow=$_POST['isFollow'];	$followid=$_POST['Classname'];	if($isFollow==0){    try{    $sth=$dbh->prepare("insert into user_follow(id,follow) value($id,$followid)");//実行する		$sth->execute();}catch(PDOException $e){	print"エラー:".$e->getMessage()."<br/>";	die();	}}else{		try{    $sth=$dbh->prepare("delete from user_follow where follow=$followid");//実行する		$sth->execute();}catch(PDOException $e){	print"エラー:".$e->getMessage()."<br/>";	die();	}}}else{    echo 'The parameter of "get_isFollow" is not found.';}?>?>